(function () {
    'use strict';

    angular.module('quiz')
        .directive('progressControl', progressControl);

    function progressControl() {
        return {
            restrict: 'E',
            template: '<canvas></canvas>',
            scope: {
                progress: '=',
                masteryScore: '='
            },
            link: function($scope, $element) {
                $scope.$watch(['progress', 'masteryScore'], function() {
                    createProgressControl($scope, $element);
                });
            }
        };
    }

    function createProgressControl(scope, element) {
        loadArrowImages(scope);
        removeFontPreloadelements();

        var canvas = $(element).children('canvas');
        canvas.attr('width', '600');
        canvas.attr('height', '250');
        canvas.attr('data-score', scope.progress);
        canvas.attr('data-masteryScore', scope.masteryScore);

        var context = canvas[0].getContext('2d');
        context.clearRect(0, 0, 600, 250);

        buildProgressControl(scope, context);
    }

    function loadArrowImages(scope) {
        scope.leftArrow = new Image();
        scope.leftArrow.src = 'data:image/png;base64,';
        scope.rightArrow = new Image();
        scope.rightArrow.src = 'data:image/png;base64,';
    }

    function removeFontPreloadelements() {
        $('.fontPreload').remove();
    }

    function buildProgressControl(scope, context) {
        var color = (scope.progress >= scope.masteryScore) ? '#4cbae6' : '#d24a45',
            progressAngle = 2 * Math.PI * (scope.progress / 100) - 0.5 * Math.PI,
            masteryScoreAngle = 2 * Math.PI * (scope.masteryScore / 100) - 0.5 * Math.PI,
            circleX = 300, circleY = 125, circleRadius = 82;

        //  drawing outer circle with border
        drawCircle(context, circleX, circleY, circleRadius, '#f0f0f0', 'rgba(0, 0, 0, 0.3)', 2);

        //  drawing inner circle with border
        drawCircle(context, circleX, circleY, circleRadius - 6, '#f0f0f0', 'rgba(0, 0, 0, 0.3)', 2);

        //  drawing circle background
        drawCircle(context, circleX, circleY, circleRadius - 3, '#f0f0f0', '#f0f0f0', 4);

        //  drawing circle
        drawCircle(context, circleX, circleY, circleRadius - 3, color, color, 6, progressAngle);

        // draw score text
        var scoreTextColor = scope.progress >= scope.masteryScore ? '#4cbae6' : '#f16162';

        drawText(context, scope.progress + '%', '40px robotoslabbold', scoreTextColor, circleX + 6, circleY + 2, 'center', 160);

        // draw course status text
        var statusText = scope.progress >= scope.masteryScore ? 'success' : 'failed',
            statusTextColor = scope.progress >= scope.masteryScore ? '#4cbae6' : '#f16162';

        drawText(context, statusText.toUpperCase(), '19px robotoslabbold', statusTextColor, circleX + 2, circleY + 36, 'center', 160);

        // draw mastery score circle
        var masteryScoreX = circleX + (Math.cos(masteryScoreAngle) * (circleRadius - 2)),
            masteryScoreY = circleY + (Math.sin(masteryScoreAngle) * (circleRadius - 2)),
            masteryScoreRadius = 17;

        drawFillCircle(context, masteryScoreX, masteryScoreY, masteryScoreRadius, 'rgba(0, 0, 0, 0.3)', 'rgba(0, 0, 0, 0.3)', '#f0f0f0', 2);

        // draw mastery score text
        drawText(context, scope.masteryScore + '%', '13px robotoslabregular', '#222', masteryScoreX, masteryScoreY + 4, 'center', 34);
        
        // draw mastery score hint text
        var masteryScoreHintTextX = (scope.masteryScore >= 50) ? masteryScoreX - 185 : masteryScoreX + 65,
            masteryScoreHintTextY = masteryScoreY + 10;

        drawText(context, 'The minimal score', '19px Rabiohead', '#8f8f8f', masteryScoreHintTextX, masteryScoreHintTextY, 'left');
        drawText(context, 'you need to succeed', '19px Rabiohead', '#8f8f8f', masteryScoreHintTextX, masteryScoreHintTextY + 20, 'left');

        // draw mastery score arrow
        var masteryScoreArrowX = scope.masteryScore >= 50 ? masteryScoreX - 63 : masteryScoreX + 22,
            masteryScoreArrowY = scope.masteryScore >= 50 ? masteryScoreY - 5 : masteryScoreY - 8,
            arrow = scope.masteryScore >= 50 ? scope.leftArrow : scope.rightArrow;

        arrow.onload = function() {
            context.beginPath();
            context.drawImage(arrow, masteryScoreArrowX, masteryScoreArrowY);
            context.closePath();
        };
    }

    function drawCircle(context, circleX, circleY, circleRadius, color, shadowColor, lineWidth, angle) {
        if (angle == undefined) {
            angle = 1.5 * Math.PI;
        }

        context.beginPath();
        context.arc(circleX, circleY, circleRadius, -0.5 * Math.PI, angle, false);
        context.shadowColor = shadowColor;
        context.shadowBlur = 1;
        context.shadowOffsetX = 1;
        context.shadowOffsetY = 1;
        context.lineWidth = lineWidth;
        context.strokeStyle = color;
        context.stroke();
        context.closePath();
    }

    function drawFillCircle(context, circleX, circleY, circleRadius, color, shadowColor, fillColor, lineWidth) {
        context.beginPath();
        context.arc(circleX, circleY, circleRadius, 0, 2 * Math.PI, false);
        context.shadowColor = shadowColor;
        context.shadowBlur = 1;
        context.shadowOffsetX = 1;
        context.shadowOffsetY = 1;
        context.lineWidth = lineWidth;
        context.strokeStyle = color;
        context.stroke();
        context.fillStyle = fillColor;
        context.fill();
        context.closePath();
    }

    function drawText(context, text, font, color, positionX, positionY, align, maxWidth) {
        context.beginPath();
        context.font = 'normal normal ' + font;
        context.fillStyle = color;
        context.textAlign = align;
        if (maxWidth) {
            context.fillText(text, positionX, positionY, maxWidth);
        } else {
            context.fillText(text, positionX, positionY);
        }
        context.closePath();
    }

}());